import React from "react";

export default function Question(props) {
  const [isHeld, setIsHeld] = React.useState(false);
  const answers = [...props.incorrect_answers, props.correct_answer];
  let questionTranslated = props.question;
  questionTranslated = questionTranslated
    .replace(/(&quot\;)/g, '"')
    .replace("&#039;", "'")
    .replace("&eacute;", "e");

  function answerAction() {
    const casa = document.getElementById(this.id);
    console.log(casa);
  }
  return (
    <div className="questions-table">
      <p className="question-header">{questionTranslated}</p>
      <div className="answer" >
      </div>
    </div>
  );
}
