import React from "react";
import "./App.css";
import Start from "./Start";
import Question from "./Question";
import Bubbles from "./Bubbles";
import { nanoid } from "nanoid";
import Answers from "./Answers";

function App() {
  console.log('rendered')
  const [startGame, setStartGame] = React.useState(false);
  const [questions, setQuestions] = React.useState([]);
  const [isHeld, setIsHeld] = React.useState(false);
  const [answer, setAnswer] = React.useState([]);
  // let answers = [];

  React.useEffect(() => {
    async function getQuestion() {
      const res = await fetch("https://opentdb.com/api.php?amount=4");
      const data = await res.json();
      setQuestions(data.results);
    }
    getQuestion();
  }, []);

  function startQuiz() {
    // setStartGame((prevStartGame) => !prevStartGame);
    setStartGame(!startGame)
  }

  function holdAnswer(id) {
    // setAnswer(
    //   answer.map((answer) => {
    //     return answer.id === id
    //       ? { ...answer, isHeld: !answer.isHeld }
    //       : answer;
    //   })
    // );
    console.log(id);
  }

  React.useEffect(() => {
    const allQuestions = questions.map((ques) => {
      let questionTranslated = ques.question
        .replace(/(&quot\;)/g, '"')
        .replace("&#039;", "'")
        .replace("&eacute;", "e");
      let answersWithoutId = [...ques.incorrect_answers, ques.correct_answer];
      if (startGame) {
        setAnswer(
          answersWithoutId.map((answer) => {
            return { value: answer, id: nanoid(), isHeld: false };
          })
        );
      }
      return (
        <>
          <Question question={questionTranslated} key={nanoid()} />
          <div className="answers-container">
            {answer.map((answer) => (
              <Answers
                isHeld={answer.isHeld}
                answer={answer.value.replace("&#039;", "s").replace("&lrm;", "")}
                key={answer.id}
                hold={() => holdAnswer(answer.id)}
              />
            ))}
          </div>
        </>
      );
    });

  }, []) 

  return (
    <div className="App">
      {!startGame ? <Start startQuiz={startQuiz} /> : allQuestions}
      {startGame && <button className="check-answers">Check answers</button>}
      <Bubbles />
    </div>
  );
}

export default App;

