import { nanoid } from "nanoid";
import React from "react";

export default function Question(props) {
  const styles = {
        backgroundColor: props.isHeld ? "#59E391" : "white"
    }
  const answers = [...props.incorrect_answers, props.correct_answer];
  let questionTranslated = props.question
  questionTranslated = questionTranslated.replace(/(&quot\;)/g,"\"").replace("&#039;","'")
  return (
    <div className="questions-table">
      <p className="question-header">{questionTranslated}</p>
      <div className="answer" style={styles}>

      </div>
    </div>
  );
}
