import React from "react"

export default function Answers(props) {
    const [isHeld, setIsHeld] = React.useState(false);
    const styles = {
        backgroundColor: isHeld ? "#59E391" : "white",
    }
    return (
            <div className="par-div" style={styles} onClick={props.answerAction}>
              <p className="individual-answer">
                {props.answer.replace("&#039;", "s").replace("&lrm;", "")}
              </p>
            </div>
    )
}
