import React from "react";
import "./App.css";
import Start from "./Start";
import Question from "./Question";
import Bubbles from "./Bubbles";
import { nanoid } from 'nanoid'

function App() {
  const [startGame, setStartGame] = React.useState(false);
  const [questions, setQuestions] = React.useState([]);

  React.useEffect(() => {
    async function getQuestion() {
      const res = await fetch("https://opentdb.com/api.php?amount=4");
      const data = await res.json();
      setQuestions(data.results);
    }
    getQuestion();
  }, []);

  function startQuiz() {
    setStartGame((prevStartGame) => !prevStartGame);
    console.log(startGame);
  }


  const allQuestions = questions.map(ques => <Question key={nanoid()} correct_answer={ques.correct_answer} question={ques.question} incorrect_answers={ques.incorrect_answers} />)



  return (
    <div className="App">
      {/* <Start startQuiz={startQuiz} /> */}
      {!startGame ? <Start startQuiz={startQuiz} /> : allQuestions}
      <button>Check answers</button>
      <Bubbles />
    </div>
  );
}

export default App;
